import UIKit

protocol MainBusinessLogic {
    func setupView()
    
    // MARK: Posts
    func didSelectedItemAt(index: Int)
    func getPostsCount() -> Int
    func getPostCellFor(index: Int) -> Main.Models.PostCellModel
}

protocol MainDataStore {
    var posts: [Post]? { get set }
    var selectedPost: Post? { get set }
}

class MainInteractor: MainBusinessLogic, MainDataStore {
    
    var presenter: MainPresentationLogic?
    var worker: MainWorker?
    var posts: [Post]?
    var selectedPost: Post?
    
    // MARK: Business Logic
    
    func setupView() {
        
        worker = MainWorker()
        
        let response = Main.SetupView.Response()
        presenter?.setupView(response: response)
        
        getInitialData()
    }
    
    func didSelectedItemAt(index: Int) {
        guard let post = posts?.getElement(index) else { return }
        
        selectedPost = post
        
        let response = Main.DidSelectedItem.Response(post: selectedPost)
        
        presenter?.presentPostDetails(response: response)
    }
    
    func getPostsCount() -> Int {
        return posts?.count ?? 0
    }
    
    func getPostCellFor(index: Int) -> Main.Models.PostCellModel {
        guard let post = posts?.getElement(index) else { return Main.Models.PostCellModel() }
        
        return Main.Models.PostCellModel(post: post)
    }
    
    fileprivate func getInitialData() {
        
        presenter?.displayLoading(true)
        
        let deadlineTime = DispatchTime.now() + 1
        DispatchQueue.main.asyncAfter(deadline: deadlineTime) { [weak self] in
            
            self?.worker?.getPosts(completion: { (posts, error) in
                
                self?.presenter?.displayLoading(true)
                
                self?.posts = posts
                
                self?.presenter?.displayLoading(false)
                self?.presenter?.presentPosts()
            })
        }
    }
}
